function initRadio( element ) {
  element.on( 'change', function( e ) {
      var _self = $( this ),
          _parentSelf = _self.parent();
      element.parent().removeClass( 'active' );
      setTimeout( function() {
          if ( _self.prop( 'checked' ) ) _parentSelf.addClass( 'active' );
      }, 30 );
  } );
};

function initCheckbox( element, className ) {
    var _className = className || 'active';

    element.on( 'change', function( e ) {
        var _self       = $( this ),
            _parentSelf = _self.parent();
        setTimeout( function() {
            if ( _self.prop( 'checked' ) ) _parentSelf.addClass( _className );
            else _parentSelf.removeClass( _className );
        }, 30 );
    } );
};
